    function obtenerPersonaAleatoria() {
        const personasRef = database.ref('personas'); 

        personasRef.once('value')
          .then((snapshot) => {
            const personas = snapshot.val();
            const keys = Object.keys(personas);
            const randomKey = keys[Math.floor(Math.random() * keys.length)];
            const personaAleatoria = personas[randomKey];

            console.log('Persona Aleatoria:', personaAleatoria);
          })
          .catch((error) => {
            console.error('Error al obtener la persona aleatoria:', error);
          });
      }

  return (
    <div className="sorteo-page">
        <button className='boton-sortear'>SORTEAR</button>
    </div>
  )
}
